mapper.select:
select 

from route
 
	res1!='0'
 
 	and   rid = #{Route.rid,javaType=Integer}
 
 
  
 	and   rname = #{Route.rname,javaType=String}
 
  
  limit ${start},${pagesize}


	select count(rid) from route
	
   res1!='0'
   
   	and   rid = #{rid,javaType=Integer}
   
   
    
   	and   rname = #{rname,javaType=String}

mapper.delete:
update route
set res1='0'
where rid=#{rid}

mapper.insert:
insert into route ( rname, rvia, 
  res1, res2, res3, res4
  )
values ( #{rname,jdbcType=VARCHAR}, #{rvia,jdbcType=VARCHAR}, 
 '1', #{res2,jdbcType=VARCHAR}, #{res3,jdbcType=VARCHAR}, #{res4,jdbcType=VARCHAR}
  )
