mapper.select:
select 

from users
where uname=#{uname}
and upwd=#{upwd}
and ustatus=0

select 

from users
where uname=#{uname}
and upwd=#{upwd}
and ustatus=1

mapper.insert:
insert into users (usId, uname, upwd, 
  urealName, uaddress, uphone, 
  utel, uemail, ustatus, 
  res1, res2, res3, res4
  )
values (#{usid,jdbcType=INTEGER}, #{uname,jdbcType=VARCHAR}, #{upwd,jdbcType=VARCHAR}, 
  #{urealname,jdbcType=VARCHAR}, #{uaddress,jdbcType=VARCHAR}, #{uphone,jdbcType=VARCHAR}, 
  #{utel,jdbcType=VARCHAR}, #{uemail,jdbcType=VARCHAR}, #{ustatus,jdbcType=INTEGER}, 
  #{res1,jdbcType=VARCHAR}, #{res2,jdbcType=VARCHAR}, #{res3,jdbcType=VARCHAR}, #{res4,jdbcType=VARCHAR}
  )
